import api from './api';

export const ACTION_TYPES = {
    CREATE: 'CREATE',
    UPDATE: 'UPDATE',
    DELETE: 'DELETE',
    FETCH_ALL: 'FETCH'
}

export const fetchAll = () => dispatch => {
    //get api request 
    api.product().fetchAll()
    try{
        response => {
        dispatch({
            type: ACTION_TYPES.FETCH_ALL,
            payload: response.data
        })
    }}
    catch{error => console.log(error)}
}
