import styled from 'styled-components';

const Form = styled.form`
box-shadow: 0 0 5px 3px ${props => props.theme.lightGray};
background: rgba(0, 0, 0, 0.07);
border: 3px solid ${props => props.theme.darkBlue} ;
padding: 20px;
font-size: 1.5rem;
line-height: 1.5;
label {
  display: block;
  margin-bottom: 1rem;

}
input,
textarea,
select {
  width: 100%;
  padding: 0.5rem;
  font-size: 1rem;
  border: 1px solid ${props => props.theme.darkGray};
  &:focus {
    outline: 0;
    border-color: ${props => props.theme.blue};
  }
}
button,
input[type='submit'] {
  width: auto;
  background: ${props => props.theme.darkGreen};
  color: white;
  border: 0;
  font-size: 2rem;
  padding: 0.5rem 1rem;
  &:hover {
    color: ${props => props.theme.lightGray};
}
}
fieldset {
  border: 0;
  padding: 0;
  &[disabled] {
    opacity: 0.5;
  }
}
`;

export default Form;
